import axios from "axios";

const API_BASE = "http://localhost:5000"; // your backend API

export const startScan = async (repoUrl) => {
  const res = await axios.post(`${API_BASE}/start-scan`, { repo_url: repoUrl });
  return res.data; // should return { scan_id: "..." }
};

export const getScanResults = async (scanId) => {
  const res = await axios.get(`${API_BASE}/scan-status/${scanId}`);
  return res.data;
};
