import React from "react";

export default function ScanStatus({ status }) {
  const colorMap = {
    running: "text-yellow-500",
    completed: "text-green-500",
    failed: "text-red-500",
  };

  return (
    <div className="p-4">
      <h3 className="text-lg font-bold">
        Status:{" "}
        <span className={colorMap[status] || "text-gray-500"}>
          {status || "Idle"}
        </span>
      </h3>
    </div>
  );
}
