import React from "react";

export default function VulnerabilityTable({ results }) {
  if (!results) return null;

  return (
    <table className="border-collapse border border-gray-300 w-full mt-4">
      <thead>
        <tr className="bg-gray-100">
          <th className="border p-2">Severity</th>
          <th className="border p-2">Count</th>
        </tr>
      </thead>
      <tbody>
        {["critical", "high", "medium", "low", "total"].map((level) => (
          <tr key={level}>
            <td className="border p-2 capitalize">{level}</td>
            <td className="border p-2">{results[level] || 0}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
