import React, { useState, useEffect, useCallback, useMemo } from 'react';

const apiService = {
  baseUrl: 'http://localhost:3001',
  
  async startScan(repository, scanDepth = 'standard') {
    console.log('üöÄ D√©marrage du scan:', { repository, scanDepth });
    
    try {
      const response = await fetch(`${this.baseUrl}/api/start-scan`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          repository,
          scanDepth,
          userId: this.getUserId(),
          timestamp: new Date().toISOString()
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP ${response.status}: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('‚úÖ Scan d√©marr√©:', result);
      return result;
    } catch (error) {
      console.error('‚ùå Erreur d√©marrage scan:', error);
      throw error;
    }
  },

  // V√©rifier le statut d'un scan
  async getScanStatus(scanId) {
    try {
      const response = await fetch(`${this.baseUrl}/api/scan-status/${scanId}`);
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      return await response.json();
    } catch (error) {
      console.error('‚ùå Erreur statut scan:', error);
      throw error;
    }
  },

  // R√©cup√©rer les r√©sultats d'un scan
  async getScanResults(scanId) {
    try {
      const response = await fetch(`${this.baseUrl}/api/scan-results/${scanId}`);
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      return await response.json();
    } catch (error) {
      console.error('‚ùå Erreur r√©sultats scan:', error);
      throw error;
    }
  },

  // R√©cup√©rer la liste des scans
  async getScans(params = {}) {
    const { limit = 20, status = 'all', search = '' } = params;
    const queryParams = new URLSearchParams({
      limit: limit.toString(),
      status,
      search
    });

    try {
      const response = await fetch(`${this.baseUrl}/api/scans?${queryParams}`);
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      return await response.json();
    } catch (error) {
      console.error('‚ùå Erreur r√©cup√©ration scans:', error);
      throw error;
    }
  },

  // Supprimer un scan
  async deleteScan(scanId) {
    try {
      const response = await fetch(`${this.baseUrl}/api/scans/${scanId}`, {
        method: 'DELETE'
      });
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `HTTP ${response.status}`);
      }
      return await response.json();
    } catch (error) {
      console.error('‚ùå Erreur suppression scan:', error);
      throw error;
    }
  },

  // Helper pour l'ID utilisateur
  getUserId() {
    return `user_${Date.now()}`;
  }
};

function App() {
  const [githubUrl, setGithubUrl] = useState('');
  const [scans, setScans] = useState([]);
  const [currentScan, setCurrentScan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [ws, setWs] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  
  const [selectedScan, setSelectedScan] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [wsReconnecting, setWsReconnecting] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [deletingScans, setDeletingScans] = useState(new Set());
  const [selectedScansForDeletion, setSelectedScansForDeletion] = useState(new Set());
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);
  const [scanDepth, setScanDepth] = useState('standard');

  const theme = useMemo(() => ({
    bg: darkMode ? '#0f0f23' : '#f8f9fa',
    cardBg: darkMode ? '#1e1e3f' : 'white',
    text: darkMode ? '#ffffff' : '#2c3e50',
    textMuted: darkMode ? '#a0a0a0' : '#6c757d',
    border: darkMode ? '#404040' : '#e1e8ed',
    primary: '#3498db',
    success: '#27ae60',
    warning: '#f39c12',
    danger: '#e74c3c',
    info: '#17a2b8'
  }), [darkMode]);

  const addNotification = useCallback((message, type = 'info') => {
    const id = Date.now();
    const notification = { 
      id, 
      message, 
      type, 
      timestamp: new Date(),
      read: false 
    };
    
    setNotifications(prev => [notification, ...prev.slice(0, 9)]); // Max 10 notifications
    
    // Auto-suppression apr√®s 5 secondes pour les succ√®s/infos
    if (type === 'success' || type === 'info') {
      setTimeout(() => {
        setNotifications(prev => prev.filter(n => n.id !== id));
      }, 5000);
    }
  }, []);

  // ‚úÖ R√âCUP√âRATION DES SCANS AVEC RETRY
  const fetchScans = useCallback(async (retries = 3) => {
    try {
      const data = await apiService.getScans({
        limit: 50,
        status: filterStatus,
        search: searchTerm
      });
      setScans(data);
    } catch (err) {
      console.error('‚ùå Erreur r√©cup√©ration scans:', err);
      if (retries > 0) {
        setTimeout(() => fetchScans(retries - 1), 2000);
      } else {
        addNotification('Impossible de charger l\'historique', 'error');
      }
    }
  }, [filterStatus, searchTerm, addNotification]);

  useEffect(() => {
    fetchScans();
  }, [fetchScans]);

  // ‚úÖ VALIDATION URL GITHUB AM√âLIOR√âE
  const isValidGitHubUrl = (url) => {
    const githubRegex = /^https:\/\/github\.com\/[a-zA-Z0-9_.-]+\/[a-zA-Z0-9_.-]+\/?$/;
    return githubRegex.test(url.trim());
  };

  // ‚úÖ D√âMARRAGE DE SCAN AVEC GESTION D'ERREURS ROBUSTE
  const handleScan = async (e) => {
    e.preventDefault();
    
    if (!isValidGitHubUrl(githubUrl)) {
      const errorMsg = 'Veuillez entrer une URL GitHub valide (ex: https://github.com/user/repo)';
      setError(errorMsg);
      addNotification(errorMsg, 'error');
      return;
    }

    setLoading(true);
    setError(null);
    setCurrentScan(null);
    
    try {
      console.log('üöÄ D√©marrage du scan...', { githubUrl, scanDepth });
      
      const result = await apiService.startScan(githubUrl, scanDepth);
      
      if (result.success && result.scan) {
        setCurrentScan(result.scan);
        setGithubUrl('');
        addNotification(`üöÄ Scan d√©marr√© pour ${result.scan.repository}`, 'success');
        
        // D√©marrer le polling du statut
        startPolling(result.scan.scanId || result.scan.id);
      } else {
        throw new Error(result.error || 'R√©ponse invalide du serveur');
      }
    } catch (err) {
      const errorMsg = err.message || 'Erreur lors du d√©marrage du scan';
      setError(errorMsg);
      addNotification(errorMsg, 'error');
      console.error('‚ùå Erreur scan:', err);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ POLLING DU STATUT AVEC TIMEOUT
  const startPolling = useCallback(async (scanId) => {
    if (!scanId) return;
    
    const maxAttempts = 120; // 10 minutes avec 5s d'intervalle
    let attempts = 0;

    const poll = async () => {
      try {
        attempts++;
        console.log(`üîç Polling statut scan ${scanId} (${attempts}/${maxAttempts})`);
        
        const statusResponse = await apiService.getScanStatus(scanId);
        
        if (statusResponse.success && statusResponse.scan) {
          const scan = statusResponse.scan;
          setCurrentScan(scan);
          
          if (scan.status === 'completed') {
            console.log('‚úÖ Scan termin√©:', scan);
            fetchScans(); // Actualiser la liste
            return; // Arr√™ter le polling
          } else if (scan.status === 'failed') {
            console.log('‚ùå Scan √©chou√©:', scan);
            addNotification(`Scan √©chou√©: ${scan.error_message || 'Erreur inconnue'}`, 'error');
            return; // Arr√™ter le polling
          } else if (scan.status === 'running' && attempts < maxAttempts) {
            // Continuer le polling
            setTimeout(poll, 5000);
          } else if (attempts >= maxAttempts) {
            addNotification('Timeout: le scan prend plus de temps que pr√©vu', 'warning');
            return;
          }
        } else if (attempts < maxAttempts) {
          setTimeout(poll, 5000); // Retry sur erreur
        }
      } catch (error) {
        console.error('‚ùå Erreur polling:', error);
        if (attempts < maxAttempts) {
          setTimeout(poll, 5000); // Retry sur erreur
        } else {
          addNotification('Erreur de communication avec le serveur', 'error');
        }
      }
    };

    poll();
  }, [addNotification, fetchScans]);

  // ‚úÖ SUPPRESSION DE SCAN AM√âLIOR√âE
  const deleteScan = async (scanId) => {
    setDeletingScans(prev => new Set([...prev, scanId]));
    
    try {
      await apiService.deleteScan(scanId);
      
      // Mise √† jour locale imm√©diate
      setScans(prev => prev.filter(scan => scan.id !== scanId));
      setSelectedScansForDeletion(prev => {
        const newSet = new Set(prev);
        newSet.delete(scanId);
        return newSet;
      });
      
      if (selectedScan && selectedScan.id === scanId) {
        setSelectedScan(null);
      }

      addNotification('‚úÖ Scan supprim√© avec succ√®s', 'success');
    } catch (err) {
      console.error('‚ùå Erreur suppression:', err);
      addNotification(err.message || 'Erreur lors de la suppression', 'error');
    } finally {
      setDeletingScans(prev => {
        const newSet = new Set(prev);
        newSet.delete(scanId);
        return newSet;
      });
      setShowDeleteConfirm(null);
    }
  };

  // ‚úÖ FILTRAGE AVANC√â
  const filteredScans = useMemo(() => {
    return scans.filter(scan => {
      const matchesSearch = !searchTerm || 
        scan.repository?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        scan.github_url?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesFilter = filterStatus === 'all' || scan.status === filterStatus;
      return matchesSearch && matchesFilter;
    }).sort((a, b) => new Date(b.start_time) - new Date(a.start_time));
  }, [scans, searchTerm, filterStatus]);

  // ‚úÖ RELANCE DE SCAN
  const rerunScan = async (scan) => {
    const repoUrl = scan.github_url || `https://github.com/${scan.repository}`;
    setGithubUrl(repoUrl);
    setScanDepth('standard');
    
    // Scroll vers le formulaire
    window.scrollTo({ top: 0, behavior: 'smooth' });
    
    addNotification(`üìã URL copi√©e: ${scan.repository}`, 'info');
  };

  // ‚úÖ COMPOSANTS UI

  // Panel de notifications
  const NotificationPanel = () => (
    <div style={{
      position: 'fixed',
      top: '1rem',
      right: '1rem',
      zIndex: 1000,
      display: showNotifications ? 'block' : 'none',
      maxWidth: '350px'
    }}>
      {notifications.map(notification => (
        <div
          key={notification.id}
          style={{
            backgroundColor: theme.cardBg,
            border: `2px solid ${
              notification.type === 'success' ? theme.success :
              notification.type === 'error' ? theme.danger :
              notification.type === 'warning' ? theme.warning :
              theme.info
            }`,
            color: theme.text,
            padding: '1rem',
            borderRadius: '0.5rem',
            marginBottom: '0.75rem',
            boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
            animation: 'slideInRight 0.3s ease-out',
            position: 'relative'
          }}
        >
          <div style={{ 
            fontSize: '0.875rem', 
            fontWeight: '500',
            marginBottom: '0.25rem'
          }}>
            {notification.type === 'success' && '‚úÖ '}
            {notification.type === 'error' && '‚ùå '}
            {notification.type === 'warning' && '‚ö†Ô∏è '}
            {notification.type === 'info' && '‚ÑπÔ∏è '}
            {notification.message}
          </div>
          <div style={{ 
            fontSize: '0.75rem', 
            color: theme.textMuted 
          }}>
            {notification.timestamp.toLocaleTimeString('fr-FR')}
          </div>
          
          <button
            onClick={() => setNotifications(prev => prev.filter(n => n.id !== notification.id))}
            style={{
              position: 'absolute',
              top: '0.5rem',
              right: '0.5rem',
              background: 'none',
              border: 'none',
              color: theme.textMuted,
              cursor: 'pointer',
              fontSize: '1rem',
              padding: '0.25rem'
            }}
          >
            ‚úï
          </button>
        </div>
      ))}
    </div>
  );

  // Composant statut scan
  const ScanStatus = ({ scan }) => {
    const getStatusConfig = (status) => {
      switch (status) {
        case 'pending': 
          return { color: theme.warning, icon: '‚è≥', label: 'En attente' };
        case 'running': 
          return { color: theme.primary, icon: 'üîÑ', label: 'En cours' };
        case 'completed': 
          return { color: theme.success, icon: '‚úÖ', label: 'Termin√©' };
        case 'failed': 
          return { color: theme.danger, icon: '‚ùå', label: '√âchou√©' };
        default: 
          return { color: theme.textMuted, icon: '‚ùì', label: 'Inconnu' };
      }
    };

    const statusConfig = getStatusConfig(scan.status);
    const duration = scan.completed_at ? 
      Math.round((new Date(scan.completed_at) - new Date(scan.start_time)) / 1000) : 
      Math.round((Date.now() - new Date(scan.start_time)) / 1000);

    return (
      <div style={{
        backgroundColor: theme.cardBg,
        color: theme.text,
        padding: '2rem',
        borderRadius: '0.75rem',
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
        marginBottom: '2rem',
        border: `2px solid ${statusConfig.color}20`,
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'flex-start',
          gap: '1.5rem',
          marginBottom: '1.5rem'
